import React, { useEffect, useState } from 'react';
import NewsCard from './components/NewsCard';
import './styles.css';

const categories = ['general', 'business', 'entertainment', 'health', 'science', 'sports', 'technology'];

function App() {
  const [articles, setArticles] = useState([]);
  const [category, setCategory] = useState('general');
  const [query, setQuery] = useState('');

  const fetchNews = async () => {
    const apiKey = process.env.REACT_APP_NEWS_API_KEY;
    const url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=a38389b4b9e94edebb610b78a8248dcd&category=${category}&q=${query}`;

    const res = await fetch(url);
    const data = await res.json();
    setArticles(data.articles || []);
  };

  useEffect(() => {
    fetchNews();
  }, [category]);

  const handleSearch = (e) => {
    e.preventDefault();
    fetchNews();
  };

  return (
    <div className="App">
      <h1>ðŸ“° Newsphoria ðŸ“°</h1>
      <p>Stay updated with the latest news from around the globe.</p>


      <form onSubmit={handleSearch}>
        <input
          type="text"
          placeholder="Search news..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <button type="submit">Search</button>
      </form>

      <div className="categories">
        {categories.map((cat) => (
          <button key={cat} onClick={() => setCategory(cat)} className={category === cat ? 'active' : ''}>
            {cat}
          </button>
        ))}
      </div>

      <div className="news-container">
        {articles.map((article, i) => (
          <NewsCard key={i} article={article} />
        ))}
      </div>
    </div>
  );
}

export default App;
